"use strict";$(document).ready(function(){nav_scroll_down("header.topbar","down",0);nav_scroll_down("header.modbar","down",0);$('[data-action="open_rightbar"]').on("click",function(a){a.stopPropagation();$("header.rightbar").toggleClass("open")});$('[data-action="switch_account"]').on("click",function(){$.ajax({url:"/dashboard",type:"POST",data:"action=switch_account&id="+$(this).data("id"),processData:false,cache:false,dataType:"json",success:function(a){if(a.status=="success"){location.reload()}else{if(a.status=="error"){open_notification_modal("alert",a.message)}}}})});$('[data-action="logout"]').on("click",function(){$.ajax({url:"/dashboard",type:"POST",data:"action=logout",processData:false,cache:false,dataType:"json",success:function(a){location.reload()}})})});function transform_form_modal(a,b){if(a=="create"){b.find("form").find('button[type="submit"]').html('<i class="fas fa-plus"></i>');b.find("form").find('button[type="submit"]').removeClass("warning");b.find("form").find('button[type="submit"]').addClass("success")}else{if(a=="update"){b.find("form").find('button[type="submit"]').html('<i class="fas fa-pen"></i>');b.find("form").find('button[type="submit"]').removeClass("success");b.find("form").find('button[type="submit"]').addClass("warning")}}};